import React from 'react';
import {useHistory} from 'react-router-dom';

// const navigateToTeachers = () => {
//   navigate('/teachers');
// };

// const navigateToStudents = () => {
//   navigate('/students');
// };

// const navigateToCourses = () => {
//   navigate('/courses');
// };

const history = useHistory();

const tags = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  padding: '10px 10px',
  margin: '10vh 0 20vh 0',
  backgroundColor: 'rgba(94, 151, 255, 0.4)',
};

function Header() {
  return (
    <header>
      <h1>Class Management System</h1>
      <div style={tags}>
        <div>
          <h2>Teachers</h2>
          <div style={{marginTop: '30px', marginBottom: '18px'}}>
            <button onClick={() => navigateToTeachers} className="round-button">
              Add Teachers
            </button>
          </div>
        </div>
        <div>
          <h2>Students</h2>
          <div style={{marginTop: '30px', marginBottom: '18px'}}>
            <button onClick={() => navigateToStudents} className="round-button">
              Add students
            </button>
          </div>
        </div>
        <div>
          <h2>Courses</h2>
          <div style={{marginTop: '30px', marginBottom: '18px'}}>
            <button onClick={() => navigateToCourses} className="round-button">
              Add Course
            </button>
          </div>
        </div>
      </div>
    </header>
  );
}

export default Header;
